import React from 'react';
import { useNavigate } from 'react-router-dom';
import "../styles/card.sass";

function CardLodging(props) {
  const { lodging } = props;
  const navigate = useNavigate();

  return (
    <div className='logements'>
      {lodging.map((lodging) => {
        const { id, cover, title } = lodging;

        const handleClick = () => {
          // Utilise le navigate pour rediriger vers "/lodging/:id"
          navigate(`/lodging/${id}`);
        };

        return (
          <div className="lodgingElement" key={id} onClick={handleClick}>
            <div className='overlay'>
              <div className='gradient-overlay'></div> 
              <img className="imgCard" src={cover} alt={title} />
            </div>
            <h3 className="h3Card">{title}</h3>
          </div>
        );
      })}
    </div>
  );
}

export default CardLodging;
