import React from "react";
import "../../data/lodging.json";


function Tags() {
    const tags = lodging.map((lodging) => lodging.tags); // Extrayez les tags de chaque élément de lodgingData

    return (
      <div className="TagsElement">
        {tags.map((tag, index) => (
          <p key={index} className="Tags-name">
            {tag}
          </p>
        ))}
      </div>
    );
  }

  export default Tags;
