import ArrowRight from '../assets/chevronRight.png'
import ArrowLeft from '../assets/chevronLeft.png'
import React, { useState } from 'react';


function Slideshow ({ pictures }) {
    const [index, setIndex] = useState(0);
    const viewPictures = pictures.lenght-1 

    if (index <0) setIndex(viewPictures)
    if (index >viewPictures) setIndex(0)


    return ( 
        <div className='Slideshow'>
            <div className='displayPicture'>
                <img src={pictures[index]} className='imageView' key={"slide"+index} /> 

                <button onClick={() => setIndex(index - 1)}>{index}
                <img src={ArrowLeft} className='ArrowLeftPict' alt='fleche de gauche' />
                </button>
                <button onClick={() => setIndex(index + 1)}>{index}
                <img src={ArrowRight} className='ArrowRightPict' alt='fleche de droit' />
                </button>
            </div>
            <div className='Comptor'>
                {index+1}/{index-1}
            </div>
        </div>
    )}

export default Slideshow;
