import ArrowRight from '../assets/chevronRight.png'
import ArrowLeft from '../assets/chevronLeft.png'
import React, { useState } from 'react';
import lodging from '../../data/lodging.json'

function Slideshow({ pictures }) {

    const [index, setIndex] = useState(0);

    const viewPictures = pictures.length; // La longueur de pictures



    const handlePrevClick = () => {
      if (index === 0) {
        setIndex(viewPictures - 1);
      } else {
        setIndex(index - 1);
      }
    };

    const handleNextClick = () => {
      if (index === viewPictures - 1) {
        setIndex(0);
      } else {
        setIndex(index + 1);
      }
    };





    return (
      <div className='Slideshow'>
        <div className='displayPicture'>
          <img src={pictures[index]} className='imageView' key={"slide" + index} />
          <button onClick={handlePrevClick}>
            <img src={ArrowLeft} className='ArrowLeftPict' alt='flèche de gauche' />
          </button>
          <button onClick={handleNextClick}>
            <img src={ArrowRight} className='ArrowRightPict' alt='flèche de droite' />
          </button>
        </div>
        <div className='Comptor'>
          {index + 1}/{viewPictures}
        </div>
      </div>
  );
  function LodgingPage({ lodgingData }) {
    return (
      <div className='picturesaffichage'>
        {lodgingData.map((data, index) => (
          <Slideshow key={data.id} pictures={data.pictures} />
        ))}
      </div>
    );
  }
  }

  export default Slideshow;
